        return n
    else:
        return (fibonacci(n-1) + fibonacci(n-2))

def binary_search(arr, low, high, x):
    if low > high:
        return None
    mid = (high + low) // 2
    if arr[mid] == x:
        return mid
    elif arr[mid] > x:
        return binary_search(arr, low, mid - 1, x)
    else:
        return binary_search(arr, mid + 1, high, x)

